mapper.select:
select listagg(pname,',') within group (order by pname) as pname,listagg(mobil_phone,',') within group (order by mobil_phone) as mobile_phone
from OG_PERSON where pid in

    #{pid}

select group_concat(pname separator ',') AS pname,group_concat(mobil_phone separator ',') AS mobile_phone
from OG_PERSON where pid in

    #{pid}

mapper.update:
update DUTY_LOG
LOG_CONTENT = #{content}, UPDATE_TIME = #{updateTime},
where id = #{id}

mapper.select:
select u.ID,u.USER_ID,u.USER_NAME,u.USER_PHONE,u.LOG_CONTENT,u.LOG_TIME,u.UPDATE_TIME from DUTY_LOG u

     and u.USER_ID = #{userId}

order by u.LOG_TIME

select count(*) from duty_scheduling d
where d.mobile_phone = #{mobilePhone,jdbcType=VARCHAR} and d.duty_date = #{dutyDate,jdbcType=VARCHAR}




order by d.duty_date

select d.scheduling_id,d.pid,d.pname,d.mobile_phone,d.duty_date,t.typeinfo_id,t.type_name,d.type_no as account_name,
(select c.type_name from duty_typeinfo c where c.typeinfo_id=t.parent_id ) as type_no
from duty_scheduling d left join duty_typeinfo t on t.type_no=d.type_no

     and d.pname like concat('%',concat(#{pname},'%'))
     and d.mobile_phone like concat('%',concat(#{mobilephone},'%'))
     and d.duty_date >= #{startTime}
     and d.duty_date <= #{endTime}
     and d.type_no like concat('%',concat(#{typeNo},'%'))
     and d.duty_date = #{dutyDate}

order by d.type_no

mapper.update:
update duty_scheduling
pid = #{pid}, pname = #{pname}, type_no = #{typeNo}, typeinfo_id = #{typeinfoId}, mobile_phone = #{mobilephone}, duty_date = #{dutyDate}, leader = #{leader}, account_pid = #{accountPid},
where scheduling_id = #{schedulingId}

update duty_account
pid = #{pid}, account_pid = #{accountPid},
where id = #{id}

mapper.select:
select type_no,duty_number from duty_version_typeinfo
where rownum = 1
 and version_id = #{versionId}
 and type_no = #{typeNo}

select t.duty_number from duty_version v left join duty_version_typeinfo t on v.version_id = t.version_id
and v.duty_date like concat('%',concat(#{dutyDate},'%')) and t.typeinfo_id = #{typeinfoId}
limit 1

select.where:
and rownum = 1
 and typeinfo_id = #{typeinfoId}
 and mobile_phone = #{mobilephone}
 and duty_date = #{dutyDate}

mapper.select:


     and typeinfo_id = #{typeinfoId} 
     and mobile_phone = #{mobilephone} 
     and duty_date = #{dutyDate} 
     and time_shift = #{timeShift} 

limit 1

mapper.delete:
delete from duty_scheduling

     and typeinfo_id = #{typeinfoId}
     and mobile_phone = #{mobilephone}
     and duty_date = #{dutyDate}

mapper.insert:
insert into duty_scheduling(scheduling_id,pid,pname,mobile_phone,duty_date,type_no,typeinfo_id) SELECT A.* FROM (
SELECT #{item.schedulingId,jdbcType = VARCHAR}, #{item.pid,jdbcType = VARCHAR}, #{item.pname,jdbcType = VARCHAR}, #{item.mobilephone,jdbcType = VARCHAR}, #{item.dutyDate,jdbcType = VARCHAR}, #{item.typeNo,jdbcType = VARCHAR}, #{item.typeinfoId,jdbcType = VARCHAR} FROM DUAL
) A

insert into duty_account(id,pid,account_pid,scheduling_id)
SELECT A.* FROM (

    SELECT #{item.id,jdbcType = VARCHAR},#{item.pid,jdbcType = VARCHAR},
    #{item.accountPid,jdbcType = VARCHAR},#{item.schedulingId,jdbcType = VARCHAR}
    FROM DUAL

) A

mapper.select:
select * from duty_scheduling

     and duty_date >= #{startTime}
     and duty_date <= #{endTime}
     and type_no = #{typeNo}
