mapper.update:
update knowledge_titil
parent_id = #{parentId}, name = #{name}, event_type = #{eventType}, category = #{category}, sys_id = #{sysId}, status = #{status},
where id = #{id}

update knowledge_titil

    sys_id = #{sysId}

where sys_id = (select sys_id from knowledge_titil where id = #{id})

mapper.select:

		inner join og_sys os on os.sysid=k.sys_id

			and k.event_type = #{knowledgeTitle.eventType}
			and k.category = #{knowledgeTitle.category}
			and os.orgid = #{orgId}

        order by k.create_time desc nulls last



    and k.event_type = #{knowledgeTitle.eventType}
