mapper.insert:
        insert into knowledge_operation_history(  
            id,content_id,oper_id,oper_time, 
            remark,
            operation
 			)values(
            #{id},#{contentId},#{operId},#{operTime},
            #{remark},
            #{operation}
			)

mapper.select:
		select * from () a


            	 and a.content_id = #{contentId}


            	 and a.content_title like concat('%',concat(#{contentTitle},'%'))


            	 and a.oper_name like concat('%',concat(#{operName},'%'))


				and to_date(a.oper_time,'yyyy-MM-dd hh24:mi:ss') >= to_date(#{params.startTime},'yyyy-MM-dd hh24:mi:ss')


				and to_date(a.oper_time,'yyyy-MM-dd hh24:mi:ss') <= to_date(#{params.endTime},'yyyy-MM-dd hh24:mi:ss')


            	 and a.operation = #{operation}


            	 and a.event_type = #{eventType}


        order by a.oper_time desc

		select * from () a


            	 and a.content_id = #{contentId}


            	 and a.content_title like concat('%',concat(#{contentTitle},'%'))


            	 and a.oper_name like concat('%',concat(#{operName},'%'))


				and DATE_FORMAT(a.oper_time,'yyyy-MM-dd hh24:mi:ss') >= DATE_FORMAT(#{params.startTime},'yyyy-MM-dd hh24:mi:ss')


				and DATE_FORMAT(a.oper_time,'yyyy-MM-dd hh24:mi:ss') <= DATE_FORMAT(#{params.endTime},'yyyy-MM-dd hh24:mi:ss')


            	 and a.operation = #{operation}


            	 and a.event_type = #{eventType}


        order by a.oper_time desc
