SELECT a.id ,a.instance_id ,a.extra1 ,a.extra2 ,a.extra3 ,a.extra4 ,dd.pname as extra5 ,a.status ,a.created_time ,dd1.pname as created_by ,a.updated_time ,dd2.pname as updated_by ,gg3.name as report_org ,dd3.pname as report_person ,a.report_phone ,a.side_flag ,a.event_title ,a.event_info ,a.event_source ,a.org_flag ,a.system_name ,a.complain_flag ,a.urgent_flag ,a.finance_flag ,a.event_status ,a.event_status_reason ,a.init_first_level ,a.init_second_level ,a.init_three_level ,a.inf_level ,a.event_priority ,a.target_resolve_date ,gg4.name as assigned_group ,dd4.pname as assigned_person ,a.second_solve_plan ,a.solve_plan ,a.business_solve_plan ,a.event_source_system ,a.relational_system ,a.event_reason_category ,a.mointor_invalid ,a.monitor_invalid_reason ,a.event_category ,a.event_subclass ,a.event_entry ,a.event_subentry1 ,a.event_subentry2 ,a.final_first_level ,a.final_second_level ,a.final_three_level ,a.change_flag ,a.change_no ,a.inf_face ,a.inf_use ,a.inf_system ,a.inf_time_range ,gg5.name as second_deal_org ,dd5.pname as second_deal_person ,a.close_code ,a.event_level ,a.solution_valid_flag , b.submit_time, b.solve_time, b.solve_person, b.solve_org, b.solve_group, b.close_time, b.suspend_flag, b.suspend_time, b.urge_flag, b.urge_time, b.service_back_falg, b.add_msg_back_falg, b.cancel_falg, b.cancel_time, b.back_completion_flag FROM design_biz_incident a LEFT JOIN incident_sub_event b ON a.extra1 = b.event_no left join og_person dd on dd.pid=a.extra5 left join og_person dd1 on dd1.pid=a.created_by left join og_person dd2 on dd2.pid=a.updated_by left join og_person dd3 on dd3.pid=a.report_person left join og_person dd4 on dd4.pid=a.assigned_person left join og_person dd5 on dd4.pid=a.second_deal_person left join common_tree gg3 on gg3.og_id=a.report_org left join common_tree gg4 on gg4.og_id=a.assigned_group left join common_tree gg5 on gg4.og_id=a.second_deal_org
AND a.extra1 like CONCAT('%', #{designBizIncident.extra1}, '%')
AND a.status = #{designBizIncident.status}
AND a.event_status_reason = #{designBizIncident.eventStatusReason}
AND a.event_title like CONCAT('%', #{designBizIncident.eventTitle}, '%')
AND a.event_info like CONCAT('%', #{designBizIncident.eventInfo}, '%')
AND dd3.pname like CONCAT('%', #{designBizIncident.reportPerson}, '%')
AND gg3.name like CONCAT('%', #{designBizIncident.reportOrg}, '%')
AND a.report_phone = #{designBizIncident.reportPhone}
AND a.event_level = #{designBizIncident.eventLevel}
AND a.close_code = #{designBizIncident.closeCode}
AND a.org_flag = #{designBizIncident.orgFlag}
AND a.side_flag = #{designBizIncident.sideFlag}
AND a.relational_system = #{designBizIncident.relationalSystem}
AND a.event_category = #{designBizIncident.eventCategory}
AND a.event_subclass = #{designBizIncident.eventSubclass}
AND a.event_entry = #{designBizIncident.eventEntry}
AND a.event_subentry1 = #{designBizIncident.eventSubentry1}
AND a.event_subentry2 = #{designBizIncident.eventSubentry2}
AND a.event_priority = #{designBizIncident.eventPriority}
AND a.inf_face = #{designBizIncident.infFace}
AND a.init_first_level = #{designBizIncident.initFirstLevel}
AND a.init_second_level = #{designBizIncident.initSecondLevel}
AND a.init_three_level = #{designBizIncident.initThreeLevel}
AND dd4.pname like CONCAT('%', #{designBizIncident.assignedPerson}, '%')
AND gg4.name like CONCAT('%', #{designBizIncident.assignedGroup}, '%')
AND a.extra5 = #{todoPersonal}
AND a.extra5 = #{todoGroup}
AND a.event_source = #{designBizIncident.eventSource}
AND b.solve_org = #{incidentSubEvent.solveOrg}
AND b.solve_group = #{incidentSubEvent.solveGroup}
AND b.solve_person = #{incidentSubEvent.solvePerson}
AND b.suspend_flag = #{incidentSubEvent.suspendFlag}
AND b.submit_time BETWEEN #{submitStartTime} AND #{submitEndTime}
AND b.solve_time > #{solveStartTime}
AND b.solve_time < #{solveEndTime}
AND b.close_time > #{closeEndTime}
AND b.close_time < #{closeEndTime}
AND dd1.pname = #{createdBy}
AND #{createdGroupBy} in ( select g.groupid from og_group g left join og_group_person o on g.groupid = o.groupid left join og_person p on o.pid = p.pid where 1 = 1 AND p.pid = a.created_by union select distinct r.rid from og_role r left join OG_R_POST_ROLE pr on r.rid = pr.rid left join og_post p on p.postid = pr.postid left join og_r_user_post u on p.postid = u.postid WHERE u.userid = a.created_by union select o.orgid from og_person p left join OG_ORG o on p.orgid = o.orgid where p.pid = a.created_by)

