SELECT d.extra1, d.extra5, d.event_title, d.event_info, d.created_time FROM design_biz_incident d LEFT JOIN it_biz_extend i ON d.id = i.biz_id where i.source_type='Monitor' AND d.extra5 IS NOT NULL AND d.instance_id IS NOT NULL
