select t.id, t.extra1 as imCode, t.status, t.instance_id as instanceId, t.event_title, t.org_flag, t.report_org, t.report_person, t.report_phone, t.assigned_group, t.assigned_person, t.event_source, t.inf_level, t.event_priority, t.side_flag, t.finance_flag, t.urgent_flag, t.event_title, t.event_info, t.system_name, t.complain_flag, t.init_first_level, t.init_second_level, t.init_three_level, t.inf_level, t.target_resolve_date, t.second_solve_plan, t.solve_plan, t.event_reason_category, t.event_source_system, t.relational_system, t.event_category, t.event_subclass, t.event_entry, t.event_subentry1, t.event_subentry2, t.final_first_level, t.final_second_level, t.final_three_level, t.change_flag, t.change_no, t.inf_face, t.inf_use, t.inf_system, t.inf_time_range, t.second_deal_org, t.second_deal_person, t.solution_valid_flag, t.close_code, t.event_level, t.extra1 as imCode, t.extra2, t.extra3, t.extra4, t.extra5, t.created_time, t.created_by, t.updated_time, t.updated_by from ${tableName} t left join it_biz_extend d on t.extra1=d.biz_no
and t.created_time >= #{search.stratTime}
and t.created_time <= #{search.endTime}
and t.extra1 = #{search.imCode}
and t.status in
#{status}
and d.extend2 = #{search.eventNo}
and d.extend8 = #{search.transTeller}
and d.extend7 = #{search.transInstno}
order by t.created_time desc limit #{search.pageStart},#{search.pageSize}

select count(t.id)
from ${tableName} t
left join it_biz_extend d
on t.extra1=d.biz_no


        and t.created_time >= #{search.stratTime}


        and t.created_time <= #{search.endTime}



        and t.extra1 = #{search.imCode}




