mapper.select:
        select DISTINCT * from OG_PERSON p,OG_USER u
where p.PID=u.PID and u.INVALIDATION_MARK = '1' and p.P_FLAG = '1'
and   mobil_phone = #{phone,jdbcType=VARCHAR}

select p.pid,p.birthplace, p.pname,p.sex,p.mobil_phone,p.phone,p.position,p.leader,p.p_order,p.p_flag,p.email,p.orgid,og.orgname,og.level_code,p.invalidation_mark from og_person p left join og_org og on p.orgid = og.orgid where p.pname is not null
and p.pname like concat(concat('%',#{pName}),'%')
and p.orgid = #{orgId}
and p.pid = #{pId}
and p.mobil_phone like concat(concat('%',#{mobilPhone}),'%')
and p.p_flag = #{pflag}
and p.invalidation_mark = #{invalidationMark}
and og.orgname like concat(concat('%',#{orgname}),'%')
and og.LEVEL_CODE like concat(#{levelCode},'%')

mapper.update:
update og_person
set invalidation_mark = #{invalidationMark,jdbcType=VARCHAR}
where pid = #{pId,jdbcType=VARCHAR}

mapper.select:
select distinct p.* from OG_USER u,OG_PERSON p,OG_ORG a,OG_R_USER_POST up,OG_R_POST_ROLE pr

where u.PID = p.PID and p.ORGID = a.ORGID and u.USERID = up.USERID and up.POSTID = pr.POSTID

AND p.INVALIDATION_MARK = '1' AND u.INVALIDATION_MARK = '1'
AND pr.RID =  #{rId}
AND a.ORGID  =  #{orgId}

select distinct p.memo,p.pid,
p.pname,p.birthplace,p.email,p.address,p.birthday,p.orgId,p.edu,p.sex,p.mobil_phone,p.phone,p.position,p.leader,p.p_order,p.p_flag,a.orgname
from OG_USER u,
OG_PERSON p,
OG_ORG a,
OG_R_USER_POST up,
OG_R_POST_ROLE pr
where u.PID = p.PID
and p.ORGID = a.ORGID
and u.USERID = up.USERID
and up.POSTID = pr.POSTID
AND p.INVALIDATION_MARK = '1'
AND u.INVALIDATION_MARK = '1'

    and pr.RID = #{rId}


    and p.pId = #{pId}


    and up.POSTID = #{postId}


    and a.orgid = #{orgId}


    and a.orgname like concat(concat('%',#{orgname}),'%')


    and a.level_Code like concat(concat('%',#{org.levelCode}),'%')


    and p.pname like concat(concat('%',#{pName}),'%')


    and p.mobil_phone like concat(concat('%',#{mobilPhone}),'%')



    and a.orgid = #{remark}



    and p.pId != #{params.noPid}

select distinct p.*,ORGNAME ,u.userid as from OG_USER u, OG_PERSON p, OG_ORG a, OG_R_USER_POST up, OG_R_POST_ROLE pr where u.PID = p.PID and p.ORGID = a.ORGID and u.USERID = up.USERID and up.POSTID = pr.POSTID AND p.INVALIDATION_MARK = '1' AND u.INVALIDATION_MARK = '1' AND pr.RID =#{params.rId}
and p.pname like concat(concat('%',#{pName}),'%')
and a.ORGNAME like concat(concat('%',#{orgname}),'%')

select DISTINCT p.memo,p.pid, p.pname,p.birthplace,p.email,p.address,p.birthday,p.orgid,p.edu,p.sex,p.mobil_phone, p.phone,p.position,p.leader,p.p_order,p.p_flag,o.orgname from og_person p, og_r_user_post up, og_user u,og_org o where p.pid = u.pid and up.userid = u.userid and p.orgid=o.orgid and p.invalidation_mark = '1' and u.invalidation_mark = '1'
and p.orgid in
#{orgId}
and up.postid = #{params.postId}
and p.pName like concat(concat('%',#{pName}),'%')
and p.mobil_Phone like concat(concat('%',#{mobilPhone}),'%')

select p.pid,p.birthplace, p.pname,p.sex,p.mobil_phone,p.phone,p.position,p.leader,p.p_order,p.p_flag,p.email,p.orgid,og.orgname from (select op.* from og_person op where op.pid not in (select u.pid from og_user u where u.pid is not null)) p left join og_org og on p.orgid = og.orgid where p.pname is not null
and p.pname like concat(concat('%',#{pName}),'%')
and p.orgid = #{orgId}
and p.mobil_phone like concat(concat('%',#{mobilPhone}),'%')
and p.p_flag = #{pflag}
and og.orgname like concat(concat('%',#{orgname}),'%')

SELECT DISTINCT P .*, org.orgname FROM OG_USER U, OG_GROUP_PERSON G, OG_PERSON P, OG_R_USER_POST up, OG_ROLE r, og_r_post_role pr, OG_ORG org WHERE U .PID = P .PID AND G .PID = P .PID AND U .USERID = up.USERID AND P .INVALIDATION_MARK = '1' AND up.POSTID = pr.POSTID AND org.ORGID = P.ORGID
and pr.RID = #{rId}
and g.GROUPID = #{groupId}
and p.pname like concat(concat('%',#{pName}),'%')
and p.mobil_phone like concat(concat('%',#{mobilPhone}),'%')
and org.level_code like concat(#{params.levelCode},'%')

select p.memo, p.pid, p.pname, p.birthplace, p.email, p.address, p.birthday, p.orgId, p.edu, p.sex, p.mobil_phone, p.phone, p.position, p.leader, p.p_order, p.p_flag, p.invalidation_mark from og_person p
p.invalidation_mark = '1'
and p.orgId = #{orgId}
and p.mobil_phone = #{mobilPhone}
and p.pname like concat(concat('%',#{pName}),'%')

select distinct p.memo, p.pid, p.pname, p.birthplace, p.email, p.address, p.birthday, p.orgId, p.edu, p.sex, p.mobil_phone, p.phone, p.position, p.leader, p.p_order, p.p_flag, o.orgname from og_person p left join og_user u on p.pid = u.pid left join og_r_user_post ur on u.userid = ur.userid left join og_org o on p.orgid = o.orgid where p.INVALIDATION_MARK = '1' and u.INVALIDATION_MARK = '1' and ur.postid in
#{postId}
and p.pname like concat(concat('%',#{pName}),'%')
and p.mobil_phone =#{mobilPhone}

select DISTINCT p.* from OG_PERSON p,OG_USER u,OG_R_USER_POST up,OG_POST op,OG_R_POST_ROLE pr,OG_ROLE r
where p.PID = u.PID and u.USERID = UP.USERID and UP.POSTID = OP.POSTID and r.RID = pr.RID and OP.POSTID = pr.POSTID
and p.INVALIDATION_MARK = '1' and u.INVALIDATION_MARK = '1'
and OP.INVALIDATION_MARK = '1' and r.INVALIDATION_MARK  = '1'
and r.RID = #{roleId} and op.POSTID = #{postId}
/*and r.RID = '9210' and op.POSTID = '0087'*/

select p.pid,p.birthplace,
p.pname,p.sex,p.mobil_phone,p.phone,p.position,p.leader,p.p_order,p.p_flag,p.email,p.orgid,og.orgname,og.level_code,p.invalidation_mark
from og_person p
left join og_org og
on p.orgid = og.orgid
where p.pname is not null

    and p.pname like concat(concat('%',#{pName}),'%')


    and p.orgid = #{orgId}


    and p.pid = #{pId}


    and p.mobil_phone like concat(concat('%',#{mobilPhone}),'%')


    and p.p_flag = #{pflag}


    and p.invalidation_mark = #{invalidationMark}


    and og.orgname like concat(concat('%',#{orgname}),'%')


    and og.LEVEL_CODE =#{levelCode}

select distinct p.pid,p.birthplace,p.pname,p.sex,p.mobil_phone,p.phone,p.position,p.leader,p.p_order,p.p_flag,p.email,p.orgid,og.orgname,og.level_code,p.invalidation_mark from og_person p left join og_org og on p.orgid = og.orgid
and og.orgid in
#{orgId}
and p.pname like concat(concat('%',#{pName}),'%')
and p.mobil_phone =#{mobilPhone}
and p.invalidation_mark = '1'
union
select distinct p.pid,p.birthplace,p.pname,p.sex,p.mobil_phone,p.phone,p.position,p.leader,p.p_order,p.p_flag,p.email,p.orgid,og.orgname,og.level_code,p.invalidation_mark from og_person p left join og_user u on p.pid = u.pid left join og_r_user_post up on u.userid = up.userid left join og_org og on p.orgid = og.orgid
and up.postid in
#{postId}
and p.pname like concat(concat('%',#{pName}),'%')
and p.mobil_phone =#{mobilPhone}
and p.invalidation_mark = '1' and u.invalidation_mark = '1'

select distinct p.* from og_person p left join og_user u on p.pid = u.pid left join og_r_user_post up on u.userid = up.userid
and p.orgid = #{orgId}
and up.postid in
#{postId}
and p.pname like concat(concat('%',#{pName}),'%')
and p.mobil_phone =#{mobilPhone}
and p.invalidation_mark = '1' and u.invalidation_mark = '1'

select distinct p.pid,p.orgid,p.pname,p.mobil_phone from og_person p,og_r_user_post up,og_group_person gp
where up.userid=p.pid and gp.pid=p.pid
and p.invalidation_mark='1'

    and up.postid=#{postId}


    and gp.groupid=#{groupId}
