mapper.select:

where 1=1

    AND o.parent_ = #{parent_}


    AND o.name_ like '%${name_}%'


    AND o.id_ = #{id_}

order by o.id_

mapper.update:
update REG_FOLDER_INDEX

    name_ = #{name_},
    description_ = #{description_}

 where id_ = #{id_}
