mapper.select:

WHERE 1 = 1

    AND T.AM_RECEIVE_ID = #{amReceiveId}


    AND T.am_biz_Id = #{amBizId}


    AND a.am_biz_Id in

        #{amBizId}



    AND a.am_title like concat(concat('%',#{amTitle}),'%')


    AND a.am_code like concat(concat('%',#{amCode}),'%')


    AND a.current_status != #{params.currentStatus}


    AND a.current_status like concat(concat('%',#{currentStatus}),'%')


    AND p.pName like concat(concat('%',#{pName}),'%')


    AND o.orgName like concat(concat('%',#{orgName}),'%')


    AND T.IF_RECEIVE = #{ifReceive}


    AND ORG.ORGNAME like concat(concat('%',#{receiveDeptName}),'%')


    AND G.GRPNAME like concat(concat('%',#{receiveGroupName}),'%')


    AND A .CREATE_TIME >= #{params.beginTime}


    AND A .CREATE_TIME <= #{params.endTime}

ORDER BY T.UPDATE_TIME DESC


WHERE 1 = 1

    AND T.AM_RECEIVE_ID = #{amReceiveId}


    AND T.am_biz_Id = #{amBizId}


    AND T.am_biz_Id = ''


    AND a.am_biz_Id in

        #{amBizId}



    AND a.am_title like concat(concat('%',#{amTitle}),'%')


    AND a.am_code like concat(concat('%',#{amCode}),'%')


    AND a.current_status like concat(concat('%',#{currentStatus}),'%')


    AND p.pName like concat(concat('%',#{pName}),'%')


    AND o.orgName like concat(concat('%',#{orgName}),'%')


    AND T.IF_RECEIVE = #{ifReceive}


    AND ORG.ORGNAME like concat(concat('%',#{receiveDeptName}),'%')


    AND G.GRPNAME like concat(concat('%',#{receiveGroupName}),'%')


    AND T.RECEIVE_TIME >= #{params.beginTime}


    AND T.RECEIVE_TIME <= #{params.endTime}

ORDER BY T.UPDATE_TIME DESC

mapper.update:
update am_biz_receive
receive_dept = #{receiveDept}, receive_group = #{receiveGroup}, am_biz_id = #{amBizId}, receive_Time = #{receiveTime}, if_receive = #{ifReceive}, receive_reply = #{receiveReply}, invalidation_mark = #{invalidationMark}, receive_person_list = #{receivePersonList}, dealer = #{dealer}, update_time = #{editTime}
where am_receive_Id = #{amReceiveId}

update am_biz_receive
receive_dept = #{receiveDept}, receive_group = #{receiveGroup}, am_biz_id = #{amBizId}, receive_Time = #{receiveTime}, if_receive = #{ifReceive}, receive_reply = #{receiveReply}, invalidation_mark = #{invalidationMark}, receive_person_list = #{receivePersonList}, dealer = #{dealer}, update_time = #{editTime}
where AM_RECEIVE_ID = #{amReceiveId}
